import React, { useState } from 'react'
import axios from 'axios'

export default function VendorDetails() {
  const [products, setProducts] = useState([{}])
  const [productName, setProductName] = useState('')
  const [productPrice, setProductPrice] = useState('')
  const [name, setName] = useState('')
  const [address, setAddress] = useState('')
  const [dabri, setDabri] = useState(false)
  const [ablock, setABlock] = useState(false)
  const [sukhram, setSukhRam] = useState(false)
  const [addressMain, setAddressMain] = useState(false)

  const [show, setShow] = useState(false)

  return (
    <div className='vendordetails__main'>
      <div className="user__address" id='u__a' style={{ width: '32%', height: '100% ', paddingTop: '10rem', paddingBottom: '10rem' }}>
        <p>Tell us about your business</p>
        <div className="address__inputs">
          <div className="name">
            <p>Name</p>
            <input type="text" placeholder='Enter your name' onChange={(e) => {
              setName(e.target.value)
            }} />
          </div>
          <div className="address">
            <p>Address</p>
            <input type="text" placeholder='Address goes here' onChange={(e) => {
              setAddress(e.target.value)
            }} />
          </div>
          <div className="products">
            <p>Tell us about the products you sell</p>
            <div className="product__inputs">
              {products.map((product, index) => (
                <div>
                  <p>{index + 1}</p>
                  <input type="text" placeholder='Product name' onChange={(e) => {
                    setProductName(e.target.value)
                    products[index] = { name: e.target.value, price: productPrice }
                  }} style={{
                    borderRadius: ' 0.2rem',
                    marginBottom: '0.5rem',
                    marginTop: '0.5rem'
                  }} />
                  <input type="text" placeholder='Product price' onChange={(e) => {
                    setProductPrice(parseInt(e.target.value))
                    products[index] = { name: productName, price: e.target.value }
                  }} />
                </div>
              ))
              }
            </div>
            <button className='add' onClick={() => {
              setProducts(products => [...products, {}])
              console.log(products)
            }}>Add</button>
          </div>

          <button className="submit__address" onClick={() => {
            axios.post('http://localhost:8002/vendordetails', { name: name, address: address, products: products, email: localStorage.getItem('vendorEmail') })
            console.log(products)
          }}>Submit</button>
        </div>
      </div>
    </div>
  )
}
